package site.bleem.boot.security.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import javax.validation.constraints.NotBlank;@RestControllerpublic class SecurityController {    @Value("${spring.security.enabled}")    private Boolean aBoolean;    @GetMapping("/hello/{word}")    public ResponseEntity<String> returnWorld(@PathVariable("word") String word) {        return ResponseEntity.ok("hello "+word+":"+aBoolean);    }    @GetMapping("/id")    public ResponseEntity<String> idCode(@RequestParam("idCode") @Validated @NotBlank String idCode) {        return ResponseEntity.ok("hello "+idCode);    }}